document.addEventListener("DOMContentLoaded",(()=>{document.body.addEventListener("click",(e=>{if(e.target.classList.contains("eliminar")){const t=e.target.dataset.id,o=e.target.dataset.tipo;confirm("¿Estás seguro de querer eliminar este registro?")&&fetch('../../backEnd/php/'+"eliminar.php",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({id:t,tipo:o})}).then((e=>e.json())).then((e=>{"Exito"===e?(alert("Registro eliminado con éxito"),"alumno"==o?location.href="alumnos.html":location.reload()):alert("Error al eliminar el registro")})).catch((e=>console.error("Error:",e)))}}))}));