document.addEventListener("DOMContentLoaded",(function(){document.getElementById("login").addEventListener("submit",(function(e){e.preventDefault();const t=new FormData(this);fetch("../../backEnd/php/login.php",{method:"POST",body:t}).then((e=>e.json())).then((e=>{if(e.success){const t=e.id;localStorage.setItem("id",t),location.href="inicio.html"}else{const e=document.getElementById("mensaje");let t=document.createElement("p");t.innerText="Credenciales incorrectas!",e.append(t)}})).catch((e=>{console.error("Error del servidor"+e)}))}))}));