document.addEventListener("DOMContentLoaded",(function(){const e=document.getElementById("register");e.addEventListener("submit",(function(t){t.preventDefault();const n=new FormData(this);fetch("../../backEnd/php/register.php",{method:"POST",body:n}).then((e=>e.json())).then((t=>{if(t.success){const t=document.getElementById("mensaje");t.innerHTML="";let n=document.createElement("p");n.innerText="Registro exitoso!",t.append(n),e.reset()}else{const e=document.getElementById("mensaje");e.classList.remove("text-success"),e.classList.add("text-danger"),e.innerHTML="";let t=document.createElement("p");t.innerText="Email existente!",e.append(t)}})).catch((e=>{console.error("Error del servidor"+e)}))}))}));