const URL="../../backEnd/php/";document.addEventListener("DOMContentLoaded",function(){const form=document.getElementById("register");form.addEventListener("submit",function(e){e.preventDefault();function validarEmail(email){const re=/^[a-zA-Z].{4,}@/;return re.test(String(email).toLowerCase())}function validarNombre(nombre){const re=/^[a-zA-Z\s]+$/;return re.test(String(nombre).trim())}function validarPassword(password){return password.length>=8}const email=document.getElementById("email").value;const nombre=document.getElementById("nombre").value;const password=document.getElementById("password").value;if(!validarEmail(email)){alert("Por favor, ingresa un correo válido que comience con texto y tenga al menos 5 letras.");e.preventDefault();return false}if(!validarNombre(nombre)){alert("El nombre debe contener solo texto.");e.preventDefault();return false}if(!validarPassword(password)){alert("La contraseña debe tener una longitud mínima de 8 caracteres.");e.preventDefault();return false}const formData=new FormData(this);fetch(URL+"register.php",{method:"POST",body:formData}).then(res=>res.json()).then(data=>{if(data.success){const divMensaje=document.getElementById("mensaje");divMensaje.innerHTML="";let p=document.createElement("p");p.innerText="Registro exitoso!";divMensaje.append(p);form.reset()}else{const divMensaje=document.getElementById("mensaje");divMensaje.classList.remove("text-success");divMensaje.classList.add("text-danger");divMensaje.innerHTML="";let p=document.createElement("p");p.innerText="Email existente!";divMensaje.append(p)}}).catch(error=>{console.error("Error del servidor"+error)})})});